#include "srs_typedefs.h"

/*Data type definition here:*/
typedef enum
{
	SRS_CFFT_32C16,
	SRS_RFFT_32C16,
	SRS_RFFT_16C16,
	SRS_FFT_UNUSED = 0x7FFFFFFF
} SRSFftType;

typedef struct{
	SRSInt16	*dig_rev_tbl;
	void		*twiddle_tbl;
	int			n;
	SRSInt32	*fftws;
	int			fft_type;
    int         _refcount;
}SRSFftCfg;

typedef struct{
	int					FftSize;
	SRSFftType			Type;
}SRSFftCreateData;


typedef struct _SRSFftObj *SRSFftObj;

