#define DMP_VERSION (dmpMemory)
#define EMPTY_PADDING 144

inv_error_t inv_write_dmp_data(FILE *fd, const unsigned char *dmp, size_t len)
{
    inv_error_t result = INV_SUCCESS;
    int bytesWritten = 0;
    int write_size;
    char bank[256];

    if (len <= 0) {
        MPL_LOGE("Nothing to write");
        return INV_ERROR_FILE_WRITE;
    }
    else {
        MPL_LOGI("dmp firmware size to write = %d", len);
    }  

    memset(bank, 0, EMPTY_PADDING);
    memcpy(bank + EMPTY_PADDING, dmp, 256-EMPTY_PADDING);
    dmp += (256 - EMPTY_PADDING);
    len -= (256 - EMPTY_PADDING);
    bytesWritten = fwrite(bank, 1, 256, fd);

    while(len > 0) {
        if (len > 256)
            write_size = 256;
        else
            write_size = len;
            
        bytesWritten = fwrite(dmp, 1, write_size, fd);

        if (bytesWritten != write_size) {
           MPL_LOGE("bytes written (%d) don't match requested length (%d)\n",
                            bytesWritten, write_size);
           result = INV_ERROR_FILE_WRITE;
        }
        dmp += write_size;
        len -= write_size;
    }
    MPL_LOGI("dmp firmware write completed = %d", len);
    fclose(fd);

    return result;  
}

inv_error_t inv_load_dmp_lib(FILE *fd)
{
    inv_error_t result = INV_SUCCESS;
    result = inv_write_dmp_data(fd, DMP_VERSION, DMP_CODE_SIZE);
    return result;
}

inv_error_t inv_load_dmp(char *dmp_path, const char *dmp_firmware_path)
{
    inv_error_t result = INV_SUCCESS;
    int bytesWritten = 0;
    FILE *fp, *fp_read;
    int write_size, len;
    char firmware_file[100];
    char bin_file[100];
    char bank[256];

    sprintf(bin_file, "%s/inv_dmpfirmware.bin", dmp_firmware_path);
    if ((fp_read = fopen(bin_file, "rb")) == NULL) {
        MPL_LOGE("dmp firmware file open fail");
	return INV_ERROR_FILE_WRITE;
    }
    fseek(fp_read, 0L, SEEK_END);
    len = ftell(fp_read);
    fseek(fp_read, 0L, SEEK_SET);
    sprintf(firmware_file, "%s/misc_bin_dmp_firmware", dmp_path);
    if ((fp = fopen(firmware_file, "wb")) == NULL) {
        MPL_LOGE("dmp sysfs file open fail");
	fclose(fp_read);
	return INV_ERROR_FILE_WRITE;
    }
    while(len > 0) {
        if (len > 256)
		write_size = 256;
	else
		write_size = len;
	bytesWritten = fread(bank, 1, write_size, fp_read);
	bytesWritten = fwrite(bank, 1, write_size, fp);
	if (bytesWritten != write_size) {
		MPL_LOGE("bytes written (%d) don't match requested length (%d)\n",
			bytesWritten, write_size);
		result = INV_ERROR_FILE_WRITE;
        }
	len -= write_size;
    }
    fclose(fp);
    fclose(fp_read);
    return result;
}

void read_dmp_img(char *dmp_path, char* out_file)
{
    MPL_LOGI("read_dmp_img");
    FILE *fp, *fp_read;
    int i;
    int len, write_size;
    char bank[256];

    if ((fp_read = fopen(dmp_path, "rb")) < 0) {
        MPL_LOGE("dmp firmware file open fail");
	return;
    }
    fseek(fp_read, 0L, SEEK_END);
    len = ftell(fp_read);
    fseek(fp_read, 0L, SEEK_SET);
    printf("len=%d\n", len);
    if ((fp = fopen(out_file, "wt")) == NULL) {
	MPL_LOGE("error open out file:%s", out_file);
        return;
    }
    fprintf(fp, "unsigned char rec[]={\n");
    while (len > 0) {
        if (len > 256)
		write_size = 256;
	else
		write_size = len;
	fread(bank, 1, write_size, fp_read);
	for(i = 0; i < write_size; i++) {
		fprintf(fp, "0x%02x, ", bank[i]);
		if(((i + 1) % 16) == 0)
			fprintf(fp, "\n");
	}
	len -= write_size;
    }
    fprintf(fp, "};\n ");
    fclose(fp);
    fclose(fp_read);
}

